mapper.select:
select count(*) as count from product
where 1=1

    AND classid=#{classid}

SELECT pro.*, sort.cname FROM product pro INNER JOIN classification sort ON pro.classid=sort.classid
AND pro.classid=${pclassid}
LIMIT ${beginIndex},${everyPage}

SELECT *
FROM product
ORDER BY ${type} ${sort}
LIMIT 0, ${index}

SELECT * FROM product WHERE 1=1

    AND pname LIKE '%${keyname}%'


    AND classid=#{classid}
